# -------------------------
# 1. Swappable 
# -------------------------
class PowerCore:
    """
    A swappable component that provides power to the station.
    """

    def __init__(self, power_output):
        self.power_output = power_output

    def activate(self):
        """
        Simulate activating the power core.
        """
        print(f"PowerCore activated with output: {self.power_output} units.")


# -------------------------
# 2. Systems (Base + Subclasses)
# -------------------------
class System:
    """
    A base class for any system in a station module.
    """

    def __init__(self, name):
        self.name = name

    def operate(self):
        """Default operation of a generic system."""
        print(f"{self.name} system is operating in a generic manner... or is it?")


class LifeSupportSystem(System):
    """
    Manages air, water, and other life support functions.
    """

    def operate(self):
        print(f"{self.name} system: Maintaining breathable atmosphere...")


class CommunicationSystem(System):
    """
    Manages communication signals within and outside the station.
    """

    def operate(self):
        print(f"{self.name} system: Transmitting signals...")


class StorageSystem(System):
    """
    Manages storage compartments and cargo.











    """

    def operate(self):
        print(f"{self.name} system: Managing inventory...")


# -------------------------
# 3. Module (Holds Systems)
# -------------------------
class Module:
    """
    A module in the space station that can hold multiple systems.
    """

    def __init__(self, name):
        self.name = name
        self.systems = []

    def add_system(self, system):
        """
        Add a new system to this module.
        """
        self.systems.append(system)

    def status(self):
        """
        Display status of each system in this module.
        """
        print(f"--- Module: {self.name} ---")
        for system in self.systems:
            system.operate()


# -------------------------
# 4. SpaceStation (Core Composition)
# -------------------------
class SpaceStation:
    """
    Base class for a space station. It contains:
      - A PowerCore (swappable).
      - One or more Modules, each containing Systems.
      - Something else...
    """

    def __init__(self, name, power_core=None):
        self.name = name
        self.modules = []
        self.power_core = power_core

    def set_power_core(self, new_power_core):
        """
        Swap out the PowerCore at runtime.
        """
        self.power_core = new_power_core
        print(f"{self.name}: PowerCore has been replaced with a new one.")

    def add_module(self, module):
        """
        Add a module to the station.
        """
        self.modules.append(module)

    def show_status(self):
        """
        Display the overall station status, including power core and each module's systems.
        """
        print(f"\n=== Space Station: {self.name} ===")
        if self.power_core:
            self.power_core.activate()
        else:
            print("No PowerCore installed!")

        for module in self.modules:
            module.status()


# -------------------------
# 5. Specialized Subclass
# -------------------------
class ResearchStation(SpaceStation):
    """
    A specialized space station that focuses on scientific research.
    """

    def __init__(self, name, power_core=None):
        super().__init__(name, power_core)
        self.research_data = []

    def conduct_research(self, experiment_name):
        """
        Unique method for the ResearchStation that isn't in the base SpaceStation.
        """
        print(f"Conducting experiment: {experiment_name}...")
        self.research_data.append(experiment_name)
        print(f"Experiment '{experiment_name}' completed. Data stored.\n")


# -------------------------
# 6. Example Simulation
# -------------------------
if __name__ == "__main__":
    # Create a power core
    main_power_core = PowerCore(power_output=1000)

    # Create a base space station
    station = SpaceStation(name="Orion Outpost", power_core=main_power_core)

    # Create a module
    habitat_module = Module("Habitat Module")
    # Add some systems to the module
    habitat_module.add_system(LifeSupportSystem("Life Support"))
    habitat_module.add_system(CommunicationSystem("Comms"))

    # Attach the module to the station
    station.add_module(habitat_module)

    # Show initial status
    station.show_status()

    # Swap out the power core for a more powerful one
    new_power_core = PowerCore(power_output=2000)
    station.set_power_core(new_power_core)

    # Show status again to see changes
    station.show_status()

    print("\n--- Now let's try the specialized ResearchStation ---\n")

    # Create a specialized station
    advanced_power_core = PowerCore(power_output=5000)
    research_station = ResearchStation("Apollo Research Hub", advanced_power_core)

    # Create a research module with different systems
    lab_module = Module("Lab Module")
    lab_module.add_system(LifeSupportSystem("Life Support"))
    lab_module.add_system(StorageSystem("Sample Storage"))
    research_station.add_module(lab_module)

    # Show station status
    research_station.show_status()

    # Perform a unique research action
    research_station.conduct_research("Zero-Gravity Plant Growth")
    research_station.conduct_research("Radiation Shield Efficiency")

    # Replace power core on the research station
    backup_power_core = PowerCore(power_output=3000)
    research_station.set_power_core(backup_power_core)
    research_station.show_status()
